import React, { useState } from "react";
import axios from "axios";

const Form = () => {
  const [user, setUser] = useState({
    Name: "",
    Age: "",
    Address: "",
    Department: "IT",
    Salary: "",
    maritalState: "",
  });

  const { Name, Age, Address, Department, Salary, maritalState } = user;

  const handleChange = (e) => {
    setUser({ ...user, [e.target.id]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(user);
    axios.post("http://localhost:3001/user", user).then(() => {
      console.log("Submitted");
    });

    setUser({
      ...user,
      Name: "",
      Age: "",
      Address: "",
      Department: "",
      Salary: "",
      maritalState: "",
    });
  };
  return (
    <div>
      <h1>Form</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="name"
          value={Name}
          onChange={handleChange}
          id="Name"
        />
        <input
          type="number"
          placeholder="Age"
          value={Age}
          onChange={handleChange}
          id="Age"
        />
        <input
          type="text"
          placeholder="Address"
          value={Address}
          onChange={handleChange}
          id="Address"
        />
        <br />
        <select onChange={handleChange} value={Department} id="Department">
          <option id="Department">IT</option>
          <option id="Department" value="HR">
            HR
          </option>
          <option id="Department" value="Management">
            Management
          </option>
        </select>
        <br />
        <input
          type="number"
          placeholder="salary"
          value={Salary}
          onChange={handleChange}
          id="Salary"
        />
        <div>
          <input
            type="radio"
            value="Married"
            placeholder="marital status"
            id="maritalState"
            checked={maritalState === "Married"}
            onChange={handleChange}
          />
          Married
        </div>
        <div>
          <input
            type="radio"
            value="Un-Married"
            placeholder="marital status"
            id="maritalState"
            checked={maritalState === "Un-Married"}
            onChange={handleChange}
          />
          Un Married
        </div>
        <input type="submit" value="Submit" />
      </form>
    </div>
  );
};

export default Form;
