import React, { useState } from "react";

const Form = () => {
  const [user, setUser] = useState({
    Name: "",
    Age: "",
    Address: "",
    Department: "",
    Salary: "",
    maritalState: "",
  });

  const { Name, Age, Address, Department, Salary, maritalState } = user;

  const handleChange = (e) => {
    setUser({ ...user, [e.target.id]: e.target.value });
  };
  return (
    <div>
      <input type="text" value={Name} onChange={handleChange} id="Name" />
      <input type="number" value={Age} onChange={handleChange} id="Age" />
      <input type="text" value={Address} onChange={handleChange} id="Address" />
      <div onChange={handleChange}>
        <input type="radio" value="IT" name="IT" /> IT
        <input type="radio" value="HR" name="HR" /> HR
        <input type="radio" value="Management" name="Management" /> Management
      </div>
      <input type="number" value={Salary} onChange={handleChange} id="Salary" />
      <input />
    </div>
  );
};

export default Form;
