import React, { useState } from "react";

const Form = () => {
  const [user, setUser] = useState({
    Name: "",
    Age: "",
    Address: "",
    Department: "",
    Salary: "",
  });

  const [maritalState, setMaritalState] = useState("");

  const { Name, Age, Address, Department, Salary } = user;

  const handleChange = (e) => {
    console.log({ [e.target.id]: e.target.value });
    // setUser({ ...user, [e.target.id]: e.target.value });
  };
  return (
    <div>
      <form>
        <input
          type="text"
          placeholder="name"
          value={Name}
          onChange={handleChange}
          id="Name"
        />
        <input
          type="number"
          placeholder="Age"
          value={Age}
          onChange={handleChange}
          id="Age"
        />
        <input
          type="text"
          placeholder="Address"
          value={Address}
          onChange={handleChange}
          id="Address"
        />
        <select onChange={handleChange} value={Department} id="Department">
          <option id="Department" value="IT">
            IT
          </option>
          <option id="Department" value="HR">
            HR
          </option>
          <option id="Department" value="Management">
            Management
          </option>
        </select>
        <input
          type="number"
          placeholder="salary"
          value={Salary}
          onChange={handleChange}
          id="Salary"
        />
        <div onChange={handleChange}>
          <input
            type="radio"
            value="Married"
            placeholder="marital status"
            id="maritalState"
          />
          Married
          <input
            type="radio"
            value="Un-Married"
            placeholder="marital status"
            id="maritalState"
          />
          Un Married
        </div>
      </form>
    </div>
  );
};

export default Form;
