import React, { useState } from "react";

const Form = () => {
  const [user, setUser] = useState({
    Name: "",
    Age: "",
    Address: "",
    Department: "",
    Salary: "",
    maritalState: "",
  });

  const { Name, Age, Address, Department, Salary, maritalState } = user;

  const handleChange = (e) => {
    console.log({ [e.target.id]: e.target.value });
    // setUser({ ...user, [e.target.id]: e.target.value });
  };
  return (
    <div>
      <input
        type="text"
        placeholder="name"
        value={Name}
        onChange={handleChange}
        id="Name"
      />
      <input
        type="number"
        placeholder="Age"
        value={Age}
        onChange={handleChange}
        id="Age"
      />
      <input
        type="text"
        placeholder="Address"
        value={Address}
        onChange={handleChange}
        id="Address"
      />

      <select onChange={handleChange} id="Department">
        <option id="Department" value="IT">
          IT
        </option>
        <option id="Department" value="HR">
          HR
        </option>
        <option id="Department" value="Management">
          Management
        </option>
      </select>

      <input
        type="number"
        placeholder="salary"
        value={Salary}
        onChange={handleChange}
        id="Salary"
      />
      <input type="checkbox" placeholder="marital status" />
    </div>
  );
};

export default Form;
